import React from 'react';
import { withRouter } from 'react-router-dom';

// imports needed when using CSS in JS
import {
    MenuItemContainer,
    BackgroundImageContainer,
    ContentContainer,
    ContentTitle,
    ContentSubtitle
  } from './menu-item.styles';

